mapper.select:
SELECT * FROM ( SELECT board.*, m.name, COALESCE(reply_count.reply_count, 0) AS reply_count, -- 댓글 수를 가져옴 ROW_NUMBER() OVER (ORDER BY board.seq DESC) AS rown FROM board JOIN members m ON board.member_id = m.id LEFT JOIN ( -- reply 테이블과 조인하여 댓글 수 계산 SELECT board_seq, COUNT(*) AS reply_count FROM reply GROUP BY board_seq ) reply_count ON board.seq = reply_count.board_seq
board.board_code = ${board_code}
AND COALESCE(reply_count.reply_count, 0) = 0
AND COALESCE(reply_count.reply_count, 0) > 0
AND board.title LIKE '%' || #{keyword} || '%'
AND REGEXP_REPLACE( REGEXP_REPLACE(board.contents, '<[^>]+>', ''), '&nbsp;', '' ) LIKE '%' || #{keyword} || '%'
AND m.name = #{keyword}
AND (board.title LIKE '%' || #{keyword} || '%' OR REGEXP_REPLACE(board.contents, '<[^>]+>', '') LIKE '%' || #{keyword} || '%' OR m.name LIKE '%' || #{keyword} || '%' OR board.member_id = #{keyword})
) a WHERE a.rown BETWEEN ${start} AND ${end}

SELECT a.* FROM ( SELECT board.*, members.name AS name, COALESCE(reply_count.reply_count, 0) AS reply_count, -- 댓글 수를 가져옵니다 ROW_NUMBER() OVER (ORDER BY board.seq DESC) AS rown FROM board JOIN members ON board.member_id = members.id LEFT JOIN ( -- reply 테이블과 조인하여 댓글 수를 계산합니다 SELECT board_seq, COUNT(*) AS reply_count FROM reply GROUP BY board_seq ) reply_count ON board.seq = reply_count.board_seq
board.board_code = #{board_code} AND board.member_id = #{member_id}
AND COALESCE(reply_count.reply_count, 0) = 0
AND COALESCE(reply_count.reply_count, 0) > 0
AND board.title LIKE '%' || #{keyword} || '%'
AND REGEXP_REPLACE(board.contents, '<[^>]+>', '') LIKE '%' || #{keyword} || '%'
AND (board.title LIKE '%' || #{keyword} || '%' OR REGEXP_REPLACE(board.contents, '<[^>]+>', '') LIKE '%' || #{keyword} || '%' OR members.name = #{keyword})
) a WHERE a.rown BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT b.*, m.name AS name, ROW_NUMBER() OVER (ORDER BY b.seq DESC) AS rown FROM board b JOIN board_bookmark bm ON b.seq = bm.board_seq JOIN members m ON b.member_id = m.id WHERE bm.member_id = #{member_id} AND b.board_code = #{board_code}
AND b.title LIKE '%' || #{keyword} || '%'
AND REGEXP_REPLACE(b.contents, '<[^>]+>', '') LIKE '%' || #{keyword} || '%'
AND b.member_id = #{keyword}
AND m.name LIKE '%' || #{keyword} || '%'
AND (b.title LIKE '%' || #{keyword} || '%' OR REGEXP_REPLACE(b.contents, '<[^>]+>', '') LIKE '%' || #{keyword} || '%' OR m.name LIKE '%' || #{keyword} || '%' OR b.member_id = #{keyword})
) subquery WHERE subquery.rown BETWEEN #{start} AND #{end}
